<?php/** * */include (dirname(__FILE__).'/../Models/Product.php');include (dirname(__FILE__).'/../Models/Category.php');include (dirname(__FILE__).'/../Models/Stock.php');abstract class Controller{    protected $viewData = array();    protected function view ($action)    {        /* Récupérer le controlleur a partir de classe fille.*/        $controller = str_replace('Controller', '', get_class($this));                /* La vue correspondant */        $view = $controller . '/' . $action.'.phtml';        include(dirname(__FILE__).'/../Views/'.$view);    }    /*      * Rederiction vers une action      *     */    protected function redirectAction()    {        $arg_list = func_get_args();        $url = '';        if(count($arg_list) == 1) {            $url .= '?controller='.$arg_list[0];        } elseif (count($arg_list)== 2) {            $url .= '?controller='.$arg_list[0].'&action='.$arg_list[1];        } else {            $url .= '?controller='.$arg_list[0].'&action='.$arg_list[1].'&id='.$arg_list[2];        }        header('Location: '.$url);        exit;    }}